mapper.update:
update review set review_title
= #{review_title}, review_content = #{review_content} where review_id
= #{review_id}

UPDATE review
SET review_views = review_views + 1
WHERE review_id = #{review_id}

mapper.select:
SELECT review_views
FROM review
WHERE review_id = #{reviewId}

SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY r.review_id DESC) AS review_no, r.review_id, member_id, review_title, review_content, review_createdAt, review_updatedAt, review_views, ra.review_attach_id, ra.review_uuid, ra.review_file_name, ra.review_file_type FROM review r LEFT JOIN review_attach ra ON r.review_id = ra.review_id
WHERE review_title LIKE CONCAT('%', #{keyword}, '%')
) r2 WHERE r2.review_no BETWEEN #{start} AND #{end}
